var parseDataFromJSON = function parseDataFromJSON(json) {
  var html = '';
  json.blocks.forEach(function (block) {
    switch (block.type) {
      case 'header':
        html += "<h".concat(block.data.level, ">").concat(block.data.text, "</h").concat(block.data.level, ">");
        break;

      case 'paragraph':
        html += "<p>".concat(block.data.text, "</p><br />");
        break;

      case 'delimiter':
        html += '<hr />';
        break;

      case 'image':
        html += "<br /><img src=\"".concat(block.data.file.url, "\" title=\"").concat(block.data.caption, "\" /><br /><center><em>").concat(block.data.caption, "</em></center><br />");
        break;

      case 'list':
        block.data.style === 'ordered' ? html += '<ol>' : html += '<ul>';
        block.data.items.forEach(function (li) {
          html += "<li>".concat(li, "</li>");
        });
        block.data.style === 'ordered' ? html += '</ol>' : html += '</ul>';
        break;

      default:
        break;
    }
  });
  return html;
};

export default parseDataFromJSON;
